import React,{Component} from 'react';

class  Que extends Component
{

    constructor(props)
    {
        super(props);
        this.state={
          qq:[], //total questions
          quest:[], //questions selectced based on difficulty
          selans:["","","","",""] //selected answers by user
        }
    }
    componentDidMount()
    {
      fetch('/users').
      then(res=>res.json()).then(qq=>this.setState({qq})).then(()=>
      {
        var k=0;
      var t=this.props.difficulty;
      var f=[];
      for(k=0;k<this.state.qq.length;k++)
      {
        if(this.state.qq[k].t==t)
        {
          f.push(this.state.qq[k]);
        }
      }
     console.log(f);
    for (var i = f.length - 1; i > 0; i--) {
      var j = Math.floor(Math.random() * (i + 1));
      var temp = f[i];
      f[i] = f[j];
      f[j] = temp;
  }
  this.setState({quest:f});
  console.log(f);
    });
     }
    render(){
      
        return (<div>
          {this.state.quest.map((n,i)=> {
             
              
           return ( <div>
             <p>{n.q}</p><br/>
             <p><input type="radio" value={n.o1} checked={this.state.selans[i] === n.o1}
            onChange={(e) => {
              var h=this.state.selans; h[i]=e.target.value;
             this.setState({selans
               :h }
           )
            }}/>{n.o1}</p>
             <p><input type="radio" value={n.o2} checked={this.state.selans[i] === n.o2}
              onChange={(e) => {
                var h=this.state.selans; h[i]=e.target.value;
               this.setState({selans
                 :h }
             )
              }}/>{n.o2}</p>
             <p><input type="radio" value={n.o3} checked={this.state.selans[i] === n.o3}
               onChange={(e) => {
                 var h=this.state.selans; h[i]=e.target.value;
                this.setState({selans
                  :h }
              )
               }}/>{n.o3}</p>
             <p><input type="radio" value={n.o4} checked={this.state.selans[i] === n.o4}
              onChange={(e) => {
                var h=this.state.selans; h[i]=e.target.value;
               this.setState({selans
                 :h }
             )
              }}/>{n.o4}</p>
            </div>);
          }
            )}
        </div>);
    }
 
}

export default Que;
