          {this.state.quest.map((n,i)=>
            <div>
             <p>{n.q}</p><br/>
             <p><input type="radio" value={n.o1} checked={this.state.selans.q+{i} === n.o1}
             onChange={(e) =>  this.setState({
              selans : {...this.state.selans, [q+{i}]: e.target.value}
