import React,{Component} from 'react';
import $ from 'jquery';
import './question.css';
class  Que extends Component
{

    constructor(props)
    {
        super(props);
        this.state={
          qq:[], //total questions
          quest:[], //questions selectced based on difficulty
          //selected answers by user
        }
    }
    componentDidMount()
    {
      fetch('/users').then(res=>res.json()).then(qq=>this.setState({qq})).then(()=>
      {
        var k=0;
      var t=this.props.difficulty;
      var f=[];
      for(k=0;k<this.state.qq.length;k++)
      {
        if(this.state.qq[k].t===t)
        {
          f.push(this.state.qq[k]);
        }
      }
     console.log(f);
    for (var i = f.length - 1; i > 0; i--) {
      var j = Math.floor(Math.random() * (i + 1));
      var temp = f[i];
      f[i] = f[j];
      f[j] = temp;
  }
  this.setState({quest:f});
  console.log(f);
    });
     }
      fun(d)
     {
      console.log(d);
     }
     myfunc(u,v)
     {
      $("#"+u+" p button").addClass("white").removeClass("green");
      $('#'+v).addClass("green").removeClass("white");
     }
    render(){
      let selans=["","","","",""];
        return (<div>
          {this.state.quest.map((n,i)=> {
             var y="ques"+i;
              if(i<5){
                return ( <div id={y}>
                  <p>{n.q}</p><br/>
                  <p><button id={y+1} className="white" onClick={()=>{
                    
                    this.myfunc(y,y+1)}}>{n.o1}</button></p><br/>
                  <p><button id={y+2} className="white" onClick={()=>{
                    
                    this.myfunc(y,y+2)}}>{n.o2}</button></p><br/>
                  <p><button id={y+3} className="white" onClick={()=>{
                    
                    this.myfunc(y,y+3)}}>{n.o3}</button></p><br/>
                  <p><button id={y+4} className="white" onClick={()=>{
                    
                    this.myfunc(y,y+4)}}>{n.o4}</button></p><br/>
                 </div>);
              }
              else
              {
                return null;
              }
           
          }
            )}
        <button onClick={()=>this.fun(selans[2])}>SUBMIT</button></div>);
    }
 
}

export default Que;
