import React,{Component} from 'react';

class  Que extends Component
{

    constructor(props)
    {
        super(props);
        this.state={
          qq:[], //total questions
          quest:[], //questions selectced based on difficulty
          //selected answers by user
        }
    }
    componentDidMount()
    {
      fetch('/users').
      then(res=>res.json()).then(qq=>this.setState({qq})).then(()=>
      {
        var k=0;
      var t=this.props.difficulty;
      var f=[];
      for(k=0;k<this.state.qq.length;k++)
      {
        if(this.state.qq[k].t==t)
        {
          f.push(this.state.qq[k]);
        }
      }
     console.log(f);
    for (var i = f.length - 1; i > 0; i--) {
      var j = Math.floor(Math.random() * (i + 1));
      var temp = f[i];
      f[i] = f[j];
      f[j] = temp;
  }
  this.setState({quest:f});
  console.log(f);
    });
     }
      fun(d)
     {
      console.log(d);
     }
    render(){
      let selans=["","","","",""];
        return (<div>
          {this.state.quest.map((n,i)=> {
             
              if(i<5){
                return ( <div>
                  <p>{n.q}</p><br/>
                  <p><input type="radio" value={n.o1} checked={selans[i].toString() === n.o1.toString()}
                 onChange={(e) => {
                 selans[i]=e.target.value;
                 }}/>{n.o1}</p>
                  <p><input type="radio" value={n.o2} checked={selans[i].toString() === n.o2.toString()}
                   onChange={function (e){
                    selans[i]=e.target.value
                   }}/>{n.o2}</p>
                  <p><input type="radio" value={n.o3} checked={selans[i].toString() === n.o3.toString()}
                    onChange={(e) => {
                      selans[i]=e.target.value;
                    }}/>{n.o3}</p>
                  <p><input type="radio" value={n.o4} checked={selans[i].toString() === n.o4.toString()}
                   onChange={(e) => {
                    selans[i]=e.target.value;
                   }}/>{n.o4}</p>
                 </div>);
              }
           
          }
            )}
        <button onClick={()=>this.fun(selans[2])}>SUBMIT</button></div>);
    }
 
}

export default Que;
